changeSet.sql:
Create table if not exists author ( id bigserial PRIMARY KEY, name varchar NOT NULL, surname varchar NOT NULL );
Create table if not exists genre ( id bigserial PRIMARY KEY, name varchar NOT NULL );
Create table if not exists book ( id bigserial PRIMARY KEY, name varchar NOT NULL, genre_id bigint NOT NULL, FOREIGN KEY (genre_id) REFERENCES genre (id) );
Create table if not exists author_book ( book_id bigint NOT NULL, author_id bigint NOT NULL, FOREIGN KEY (book_id) REFERENCES book (id), FOREIGN KEY (author_id) REFERENCES author (id) );
Insert into author(name, surname) values ('Alexander', 'Pushkin'), ('Nikolai', 'Gogol'), ('Lev', 'Tolstoy'), ('Mikhail', 'Bulgakov'), ('Fyodor', 'Dostoevsky');
Insert into genre(name) values ('Short story'), ('Novel'), ('Comedy'), ('Drama');
Insert into book(name, genre_id) values ('War and Peace', 2), ('Crime and Punishment', 2), ('The Nose', 1), ('The Master and Margarita', 2);
Insert into author_book(book_id, author_id) values (1, 3), (2, 5), (3, 2), (4, 4);
